package com.onebill.training.day6;

public class LengthDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] integerArray = new int[10];
		int[] integerArray1 = {1,2,5};
		int [][] integerArray2 = {
				{1,2,5},
				{5,10},
		};
		System.out.println("Length of Integer Array "+integerArray.length);
		System.out.println("Length of Integer1 Array "+integerArray1.length);
		System.out.println("Length of Integer2 Array "+integerArray2.length);
		System.out.println("Length of Integer2[1] Array "+integerArray2[1].length);

		

	}

}
