package com.onebill.training.day6;

public class SimpleArrayDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] integerArray = new int[5];
		for(int i=0;i<5;i++)
		{
			integerArray[i]=i;
		}
		for(int i=0;i<5;i++)
		{
			System.out.println(integerArray[i]);
		}

	}

}
